import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { fetchPosts, deletePost, submitComment } from "./utility";

const featuredPosts = ({
  user,
  token,
  posts, setPosts,
  featuredPost, setFeaturedPost
}) => {

  const [newComment, setNewComment] = useState('')
  const history = useHistory();
  const {postId} = useParams();

  const handleClose = () => {
    // clear the state
    setFeaturedPost(false)

    // go back to vacations
    history.push('/posts');
  }

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    // submit the comment to the server, using our API thingy
    const _comment = await submitComment(token, newComment, featuredPost.id)

    const newPosts= await fetchPosts(token)
    setPosts(newPosts);
    setFeaturedPost(newPosts.find(pt => pt.id == featuredPost.id))
  }

  const handleDeletePost = async (e, postId) => {
    e.stopPropagation();
    if (window.confirm('Are you sure?')) {
      const isDeleted = await deletePost(token, postId)
      if (isDeleted) {
        handleClose()
        const remainingPosts = posts.filter(pt => pt.id != postId)
        setPosts(remainingPosts)
      }
    }
  }

  return <article className="featured-post">
    <h1>{featuredPost.title}</h1>
    <p>{featuredPost.location}</p>
    <p>{featuredPost.description}</p>
    <p>{featuredPost.price}</p>
    <p>Posted by: {featuredPost?.guest?.username}</p>

    <section>
      <h2>Comments</h2>
      <ul>
        {featuredPost.comments.map(comment => {
          return <li key={comment.id}>
            <span>{comment.content} posted by: {comment.guest.username}</span>
          </li>
        })}
      </ul>
    </section>

    <form onSubmit={handleSubmitComment}>
        <input 
          onChange={(e) => setNewComment(e.target.value)}
          type="text" name="comment" 
          placeholder="comment" value={newComment} />
          <button type="submit">Submit Comment</button>
    </form>

    <section>
      {/* Submit comment component */}
    </section>
    <button onClick={handleClose}>Close</button>
    {user && token && (featuredPost.guestId == user.id) &&
        <button onClick={(e) => {handleDeletePost(e, featuredPost.id)}}>Delete </button>
      }
  </article>
}


export default featuredPosts;
