import React, { useState } from "react";
import { NavLink, Route } from "react-router-dom";
import FeaturedPost from "./FeaturedPost";
import Account from "./";
import Home from "./home";
import Posts from "./posts";



const App = () => {
    const [posts, setPosts] = useState([]);
    const [featuredPost, setFeaturedPost] = useState(false);
    const [newPost, setNewPost] = useState(false);
    const [user, setUser] = useState(false);
    const [token, setToken] = useState("");

    return (
        <main>
            <nav>
                <NavLink
                    exact
                    to="/"
                >
                    Home
                </NavLink>

                <NavLink
                    to="/posts"
                >
                    Posts
                </NavLink>

                <NavLink
                    to="/profile"
                >
                    Profile
                </NavLink>
            </nav>

            <Route exact path="/">
                <Home user={user} />
            </Route>

            <Route path="/posts">
                <Route path="/posts/:postId">
                    {user && token && featuredPost && (
                        <FeaturedPost
                            user={user}
                            token={token}
                            posts={posts}
                            setPosts={setPosts}
                            featuredPost={featuredPost}
                            setFeaturedPost={setFeaturedPost}
                        />
                    )}
                </Route>
                <Posts
                    user={user}
                    token={token}
                    featuredPost={featuredPost}
                    setFeaturedPost={setFeaturedPost}
                    posts={posts}
                    setPosts={setPosts}
                />
            </Route>

            <Route path="/profile">
                <Account
                    user={user}
                    setUser={setUser}
                    token={token}
                    setToken={setToken}
                />
            </Route>
        </main>
    );
};

export default App;
