import React, { useState } from "react";
import { fetchRegister } from "../util/api";

const Register = ({ token, setToken }) => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [isRegistered, setIsRegistered] = useState(false)

    //const [user, setUser] = useState([]);

    const handleChangeUser = (event) => {
        setUserName(event.target.value);
    };

    const handleChangePassword = (event) => {
        setPassword(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const registeredToken = await fetchRegister(userName, password); //returning a token
        setToken(registeredToken); // storing the token in setToken as a string
        setIsRegistered(true)
        console.log(userName, password);
        console.log(token)
    };



    return (
        <>
            {isRegistered ?
                <>
                    <h1> Thank you for Signing up</h1>
                </>
                :
                <>
                    <h1>Sign up</h1>
                    <div>
                        <form  onSubmit={handleSubmit}>
                            <label>Username</label>
                            <input
                                type="text"
                                name="username"
                                value={userName}
                                onChange={handleChangeUser}
                                required
                            />
                            <label htmlFor="Password">Password</label>
                            <input
                                className="password"
                                type="password"
                                name="password"
                                value={password}
                                onChange={handleChangePassword}
                                required
                            />
                            <button className="clientButton" type="submit">
                                SIGN UP
                            </button>
                        </form>
                    </div>
                </>
            }
        </>
    );
};

export default Register;
