import React, { useEffect, useState } from "react";
import {useHistory } from "react-router-dom";
import { fetchPosts} from "./utility";


// Filter posts with a simple text matcher (no fetch call needed here)
const Posts = ({
    user,
    token,
    posts,
    setPosts,
    setFeaturedPost,
}) => {
    const [search, setSearch] = useState("");
    const [description, setDescription] = useState("");
    const [location, setLocation] = useState("");
    const [price, setPrice] = useState("");
    const [title, setTitle] = useState("");

    const history = useHistory();

    useEffect(() => (async () => setPosts(await fetchPosts(token)))(), []);

    const searchMatches = (post) => {
        return `${post.location} ${post.description} ${post.title} ${post.price}`
            .toLowerCase()
            .includes(search.toLowerCase());
    };

    return (
        <>
            <h1>Posts</h1>
            <input
                onChange={(e) => setSearch(e.target.value)}
                type="text"
                name="search"
                placeholder="search"
            />

            <section>
                {posts
                    .filter((post) => {
                        return searchMatches(post);
                    })
                    .map((post) => {
                        return (
                            <div
                                key={post.id}
                                onClick={(e) => {
                                    handlePostClick(e, post);
                                }}
                            >
                                <h3>{post.title}</h3>
                                <p>{post.location}</p>
                                <p>{post.description}</p>
                                <p>{post.price}</p>
                            </div>
                        );
                    })}
            </section>
        </>
    );
};

export default Posts;
