import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";

// See a list of all posts
// token only Create a new post
// token only Delete any post



const featuredPosts = ({
  user,
  token,
  posts, setPosts,
  featuredPost, setFeaturedPost
}) => {
  return <article className="featured-post">
    <h1>{featuredPost.title}</h1>
    <p>{featuredPost.location}</p>
    <p>{featuredPost.description}</p>
    <p>{featuredPost.price}</p>
    <p>Posted by: {featuredPost?.user?.username}</p>
    </article>
}
export default featuredPosts
