import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { fetchPosts, submitPost, deletePost } from "../api/utility";

const Posts = ({
    user,
    token,
    posts,
    setPosts,
    featuredVacation,
    setFeaturedPost,
}) => {
    const [search, setSearch] = useState("");
    const [description, setDescription] = useState("");
    const [location, setLocation] = useState("");

    const history = useHistory();

    useEffect(() => (async () => setPosts(await fetchPosts(token)))(), []);

    const searchMatches = (post) => {
        return `${post.location} ${post.description}`
            .toLowerCase()
            .includes(search.toLowerCase());
    };

    const handlePostClick = (e, post) => {
        // check if user's logged in, if not then ignore
        if (user && token) {
            setFeaturedPost(post);
            history.push(`/posts/${post.id}`);
        } else {
            alert("not logged in");
        }
    };

    const handleSubmitPost = async (event) => {
        event.preventDefault();
        // Send the needed data to the endpoint
        const newPosts = await submitPost(token, location, description);
        // Do some stuff with the data that comes back
        setPosts(newPosts);
    };

    const handleDeletePost = async (e, postId) => {
        e.stopPropagation();
        if (window.confirm("Are you sure?")) {
            // Call an api utility to delete this thing
            const isDeleted = await deletePost(token, postId);
            // update my vacations state
            // Update the vacations state by removing the vacation i just deleted
            if (isDeleted) {
                const remainingPosts = posts.filter((vc) => vc.id != postId);
                setVacations(remainingPosts);
            }
        }
    };

    return (
        <>
            <h1>Vacations</h1>

            <input
                onChange={(e) => setSearch(e.target.value)}
                type="text"
                name="search"
                placeholder="search"
            />

            {user && token && (
                <form onSubmit={handleSubmitPost}>
                    <input
                        onChange={(e) => setLocation(e.target.value)}
                        type="text"
                        name="location"
                        placeholder="location"
                        value={location}
                    />
                    <input
                        onChange={(e) => setDescription(e.target.value)}
                        type="text"
                        name="description"
                        placeholder="description"
                        value={description}
                    />
                    <button type="submit">Submit Posts</button>
                </form>
            )}

            <section>
                {posts
                    .filter((post) => {
                        return searchMatches(post);
                    })
                    .map((post) => {
                        return (
                            <div
                                key={post.id}
                                onClick={(e) => {
                                    handlePostClick(e, post);
                                }}
                                className="vacation-body"
                            >
                                <h3>{post.location}</h3>
                                <p>{post.description}</p>

                                {user && token && post.guestId == user.id && (
                                    <button
                                        onClick={(e) =>
                                            handleDeletePost(e, post.id)
                                        }
                                    >
                                        Delete post
                                    </button>
                                )}
                            </div>
                        );
                    })}
            </section>
        </>
    );
};

export default Posts;

