import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { fetchPosts, deletePost, submitComment } from "./utility";

const featuredPosts = ({
  user,
  token,
  posts, setPosts,
  featuredPost, setFeaturedPost
}) => {

  const [newComment, setNewComment] = useState('')
  const history = useHistory();
  const {postId} = useParams();

  const handleClose = () => {
    setFeaturedPost(false)

    history.push('/posts');
  }



  return <article className="featured-post">
    <h1>{featuredPost.title}</h1>
    <p>{featuredPost.location}</p>
    <p>{featuredPost.description}</p>
    <p>{featuredPost.price}</p>
    <p>Posted by: {featuredPost?.guest?.username}</p>
    </article>
}
export default featuredPosts
