import React, { useState } from "react";
import { loginUser, registerUser } from "./utility";

// Sign up for an account with username and password
// Sign in with correct username/password combination
// login and /register (this could alternatively be displayed in the profile instead of living in its own route)
// See all messages for any post for which they are the author
// See all messages they've received in a special view

const profile = ({ user, setUser, token, setToken }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isRegistering, setIsRegistering] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (isRegistering) {
            const { user, token } = await registerUser(username, password);
            setUser(user);
            setToken(token);
        } else {
            const { user, token } = await loginUser(username, password);
            setUser(user);
            setToken(token);
        }
    };

    const handleLogout = () => {
        setUser(false);
        setToken("");
    };

    const toggleRegistration = () => {
        setIsRegistering(!isRegistering);
    };

    return (
        <>
            {user && token ? (
                <>
                    <h1>Profile</h1>
                    <p>{user.username} is logged in</p>
                    <button onClick={handleLogout}>Logout</button>
                </>
            ) : (
                <>
                    <h1>{isRegistering ? "Registration" : "Login"}</h1>
                    <form onSubmit={handleSubmit}>
                        <input
                            onChange={(event) =>
                                setUsername(event.target.value)
                            }
                            required
                            name="username"
                            type="text"
                            placeholder="username"
                            value={username}
                        ></input>
                        <input
                            onChange={(event) =>
                                setPassword(event.target.value)
                            }
                            required
                            name="password"
                            type="password"
                            placeholder="password"
                            value={password}
                        ></input>
                        <button type="submit">Submit</button>
                    </form>
                    <button onClick={toggleRegistration}>
                        Register/Login 
                    </button>
                </>
            )}
        </>
    );
};

export default profile;



