import React, { useState } from "react";
import { Link } from "react-router-dom";
import { fetchLogin } from "../util/api";

const Login = ({ token, setToken, userName, setUserName }) => {
    const [password, setPassword] = useState("");
    const [isLoggedin, setIsLoggedin] = useState(false)

    const handleChangeUser = (event) => {
        setUserName(event.target.value);
    };

    const handleChangePassword = (event) => {
        setPassword(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const loggedToken = await fetchLogin(userName, password); 
        setToken(loggedToken);
        setUserName(userName);
        if (loggedToken) {
            setIsLoggedin(true)
        }

    };

    return (
        <>
            {isLoggedin ? (
                <>
                    <h1 id="activeAccount">Welcome Back {userName} !</h1>
                </>
            ) : (
                <>
                    <h1 id="login">Login</h1>
                    <div id="form-container">
                        <form onSubmit={handleSubmit}>
                            <label htmlFor="Username">Username</label>
                            <input
                                type="text"
                                name="username"
                                value={userName}
                                onChange={handleChangeUser}
                                required
                            />
                            <label htmlFor="Password">Password</label>
                            <input
                                className="password"
                                type="password"
                                name="password"
                                value={password}
                                onChange={handleChangePassword}
                                required
                            />
                            <button className="clientButton" type="submit">
                                LOG IN
                            </button>
                            <div id="login-break">
                                <h6>or</h6>
                            </div>
                            <Link id="link-btn" to="/">
                                <button id="signup-Button" type="submit">Sign up</button>
                            </Link>

                        </form>
                    </div>
                </>
            )}
        </>
    );
};

export default Login;
