import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteVacation, fetchVacations, submitVacation } from "../utils/api";

const Posts = ({
  user, token,
  posts, 
  setVacations, 
  featuredVacation, setFeaturedVacation}) => {

  const [search, setSearch] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');

  const history = useHistory();

  useEffect(() => (async () => setVacations(await fetchVacations(token)))(), [])

  const searchMatches = (vacation) => {
    return `${vacation.location} ${vacation.description}`
    .toLowerCase()
    .includes(search.toLowerCase())
  }

  const handleVacationClick = (e, vacation) => {
    // check if user's logged in, if not then ignore
    if (user && token) {
      setFeaturedVacation(vacation);
      history.push(`/vacations/${vacation.id}`)
    } else {
      alert("not logged in")
    }
  }

  const handleSubmitVacation = async (event) => {
    event.preventDefault();
    // Send the needed data to the endpoint
    const newVacations = await submitVacation(token, location, description);
    // Do some stuff with the data that comes back
    setVacations(newVacations);
  }

  const handleDeleteVacation = async (e, vacationId) => {
    e.stopPropagation();
    if (window.confirm('Are you sure?')) {
      // Call an api utility to delete this thing
      const isDeleted = await deleteVacation(token, vacationId)
      // update my vacations state
      // Update the vacations state by removing the vacation i just deleted
      if (isDeleted) {
        const remainingVacations = posts.filter(vc => vc.id != vacationId)
        setVacations(remainingVacations)
      }
    }
  }

  return <>
    <h1>Vacations</h1>

    <input 
      onChange={(e) => setSearch(e.target.value)}
      type="text" name="search" placeholder="search"
    />

    {user && token && <form onSubmit={handleSubmitVacation}>
        <input 
          onChange={(e) => setLocation(e.target.value)}
          type="text" name="location"
          placeholder="location" value={location} />
        <input 
          onChange={(e) => setDescription(e.target.value)}
          type="text" name="description"
          placeholder="description" value={description} />
        <button type="submit">Submit Vacation</button>
      </form>
    }

    <section>
      {
        posts.filter(post => {
          return searchMatches(post)
        }).map(vacation => {
          return <div key={post.id} onClick={(e) => {handlepostClick(e, post)}} className="vacation-body">
            <h3>{post.location}</h3>
            <p>{post.description}</p>

            {user && token && (post.guestId == user.id) &&
              <button onClick={(e) => handleDeleteVacation(e, post.id)}>Delete post</button>
            }
          </div>
        })
      }
    </section>
  </> 
}

export default Posts;
