import React, { useState } from "react";
import { NavLink, Route } from "react-router-dom";
import Account from "./Account";
import Home from "./Home";
import Vacations from "./Vacations";
import FeaturedVacation from "./FeaturedVacation";

/**
 * Demo API Docs: 
 * https://github.com/FullstackAcademy/UNIV_Adventure_Away_API/blob/main/api_docs.md
 * 
 * Requirements:
 *  Unauthenticated:
 *    x View vacations
 *      x Search/filter (local)
 *    x log in
 *    x register
 *  
 *  Authenticated:
 *    x View vacations
 *      x Search/filter
 *    x View individual vacation
 *      x Close the vacation
 *      x View comments
 *      x Submit a new comment
 *        x Be able to see new comment immediately after you submit it
 *    x Submit a new vacation
 *    x Delete a vacation (only one you've created)
 *    x view profile
 *    x log out
 * 
 */

const App = () => {

  const [vacations, setVacations] = useState([])
  const [featuredVacation, setFeaturedVacation] = useState(false);
  const [newVacation, setNewVacation] = useState(false);
  const [user, setUser] = useState(false);
  const [token, setToken] = useState('')

  return <main>

    <nav>
      <NavLink exact to="/" className="navlink" activeClassName="active">
        Home
      </NavLink>

      <NavLink to="/vacations" className="navlink" activeClassName="active">
        Vacations
      </NavLink>

      <NavLink to="/login" className="navlink" activeClassName="active">
        Account
      </NavLink>
    </nav>

    <Route exact path="/">
      <Home user={user} />
    </Route>

    <Route path="/vacations">
      <Route path="/vacations/:vacationId">
        {user && token && featuredVacation &&
          <FeaturedVacation 
            user={user}
            token={token}
            vacations={vacations} setVacations={setVacations}
            featuredVacation={featuredVacation} 
            setFeaturedVacation={setFeaturedVacation} 
          />
        }
      </Route>
      <Vacations 
        user={user} token={token}
        featuredVacation={featuredVacation} 
        setFeaturedVacation={setFeaturedVacation}
        vacations={vacations} setVacations={setVacations} 
      />
    </Route>

    <Route path="/login">
      <Account 
        user={user} setUser={setUser} 
        token={token} setToken={setToken}
      />
    </Route>
  </main>
}

export default App;
