import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { fetchPosts, submitPost, deletePost } from "./utility";

const Posts = ({
    user,
    token,
    posts,
    setPosts,
    setFeaturedPost,
}) => {
    const [search, setSearch] = useState("");
    const [description, setDescription] = useState("");
    const [location, setLocation] = useState("");
    const [price, setPrice] = useState("");
    const [title, setTitle] = useState("");

    const history = useHistory();

    useEffect(() => (async () => setPosts(await fetchPosts(token)))(), []);

    const searchMatches = (post) => {
        return `${post.location} ${post.description} ${post.title} ${post.price}`
            .toLowerCase()
            .includes(search.toLowerCase());
    };

    const handlePostClick = (e, post) => {
        if (user && token) {
            setFeaturedPost(post);
            history.push(`/posts/${post.id}`);
        } else {
            alert("not logged in");
        }
    };

    const handleSubmitPost = async (event) => {
        event.preventDefault();
        const newPosts = await submitPost(token, location, description, title, price);
        setPosts(newPosts);
    };

    const handleDeletePost = async (e, postId) => {
        e.stopPropagation();
        if (window.confirm("Are you sure?")) {
            const isDeleted = await deletePost(token, postId);
            if (isDeleted) {
                const remainingPosts = posts.filter((vc) => vc.id != postId);
                setPosts(remainingPosts);
            }
        }
    };

    return (
        <>
            <h1>Posts</h1>

            <input
                onChange={(e) => setSearch(e.target.value)}
                type="text"
                name="search"
                placeholder="search"
            />

            {user && token && (
                <form onSubmit={handleSubmitPost}>
                    <input
                        onChange={(e) => setTitle(e.target.value)}
                        type="text"
                        name="title"
                        placeholder="title"
                        value={title}
                    />
                    <input
                        onChange={(e) => setPrice(e.target.value)}
                        type="text"
                        name="price"
                        placeholder="price"
                        value={price}
                    />
                    <input
                        onChange={(e) => setLocation(e.target.value)}
                        type="text"
                        name="location"
                        placeholder="location"
                        value={location}
                    />
                    <input
                        onChange={(e) => setDescription(e.target.value)}
                        type="text"
                        name="description"
                        placeholder="description"
                        value={description}
                    />
                    <button type="submit">Submit Posts</button>
                </form>
            )}

            <section>
                {posts
                    .filter((post) => {
                        return searchMatches(post);
                    })
                    .map((post) => {
                        return (
                            <div
                                key={post.id}
                                onClick={(e) => {
                                    handlePostClick(e, post);
                                }}
                            >
                                <h3>{post.title}</h3>
                                <p>{post.location}</p>
                                <p>{post.description}</p>
                                <p>{post.price}</p>

                                {user && token && post.guestId == user.id && (
                                    <button
                                        onClick={(e) =>
                                            handleDeletePost(e, post.id)
                                        }
                                    >
                                        Delete
                                    </button>
                                )}
                            </div>
                        );
                    })}
            </section>
        </>
    );
};

export default Posts;
