import React, { useState } from "react";
import { NavLink, Route } from "react-router-dom";
import Account from "./profile";
import Home from "./home";
import Posts from "./posts";
import featuredPosts from "./featuredPosts";

/**
 * Demo API Docs:
 * https://github.com/FullstackAcademy/UNIV_Adventure_Away_API/blob/main/api_docs.md
 *
 * Requirements:
 *  Unauthenticated:
 *    x View vacations
 *      x Search/filter (local)
 *    x log in
 *    x register
 *
 *  Authenticated:
 *    x View vacations
 *      x Search/filter
 *    x View individual vacation
 *      x Close the vacation
 *      x View comments
 *      x Submit a new comment
 *        x Be able to see new comment immediately after you submit it
 *    x Submit a new vacation
 *    x Delete a vacation (only one you've created)
 *    x view profile
 *    x log out
 *
 */

const App = () => {
    const [posts, setPosts] = useState([]);
    const [featuredPost, setFeaturedPost] = useState(false);
    const [newPost, setNewPost] = useState(false);
    const [user, setUser] = useState(false);
    const [token, setToken] = useState("");

    return (
        <main>
            <nav>
                <NavLink
                    exact
                    to="/"
                    className="navlink"
                    activeClassName="active"
                >
                    Home
                </NavLink>

                <NavLink
                    to="/posts"
                    className="navlink"
                    activeClassName="active"
                >
                    Posts
                </NavLink>

                <NavLink
                    to="/profile"
                    className="navlink"
                    activeClassName="active"
                >
                    Profile
                </NavLink>
            </nav>

            <Route exact path="/">
                <Home user={user} />
            </Route>

            <Route path="/posts">
                <Route path="/posts/:postId">
                    {user && token && featuredPost && (
                        <featuredPost
                            user={user}
                            token={token}
                            posts={posts}
                            setPosts={setPosts}
                            featuredPost={featuredPost}
                            setFeaturedPost={setFeaturedPost}
                        />
                    )}
                </Route>
                <Posts
                    user={user}
                    token={token}
                    featuredPost={featuredPost}
                    setFeaturedPost={setFeaturedPost}
                    posts={posts}
                    setPosts={setPosts}
                />
            </Route>

            <Route path="/profile">
                <Account
                    user={user}
                    setUser={setUser}
                    token={token}
                    setToken={setToken}
                />
            </Route>
        </main>
    );
};

export default App;
